import{_ as i,c as t,a5 as e,o as a}from"./chunks/framework.CHhwEXkI.js";const h=JSON.parse('{"title":"USTC L1","description":"","frontmatter":{"date":"2024-02-26T00:00:00.000Z","title":"USTC L1","status":"DONE","author":["AllenYGY"],"tags":["NOTE","Network","USTC","Lec1"],"created":"2024-03-19T00:00:00.000Z","updated":"2024-03-22T00:00:00.000Z","publish":true},"headers":[],"relativePath":"posts/Computer Network/Lecture/USTC L1.md","filePath":"posts/Computer Network/Lecture/USTC L1.md","lastUpdated":null}'),o={name:"posts/Computer Network/Lecture/USTC L1.md"};function u(r,l,s,n,c,d){return a(),t("div",null,l[0]||(l[0]=[e('<h1 id="ustc-l1" tabindex="-1">USTC L1 <a class="header-anchor" href="#ustc-l1" aria-label="Permalink to &quot;USTC L1&quot;">​</a></h1><h2 id="互联网" tabindex="-1"><strong>互联网</strong> <a class="header-anchor" href="#互联网" aria-label="Permalink to &quot;**互联网**&quot;">​</a></h2><ul><li>网络 <ul><li>节点与边构成的大小形状无关的拓扑</li><li>电话网，社交网络....</li></ul></li><li>计算机网络---具体构成角度 <ul><li>联网计算机构成的系统</li><li>节点 <ul><li>主机节点，及其运行的应用程序 ⬛</li><li>数据交换节点 ⭕️ <ul><li>路由器、交换机等网络设备 既不是源也不是目标，转发数据</li><li>根据层次不同可分 <ul><li>中继器</li><li>交换机 链路层</li><li>路由器 网络层</li><li>负载均衡设备 更高层</li></ul></li></ul></li></ul></li><li>边：通信链路 <ul><li>接入网链路^[Access]：主机连接到互联网的链路</li><li>骨干链路^[Backbone]：路由器间的链路</li></ul></li><li>协议： <ul><li>对等层实体在通信过程当中的应该遵守的规则的集合，包括语法、语义和时序</li><li>协议定义了两个或多个通信实体之间交换的报文格式和次序，以及在报文传输和/或接收/或其他事件方面所采取的动作 <ul><li>PDU：协议数据单元</li></ul></li><li>按照层次不同 <ul><li>物理层协议</li><li>链路层协议 <ul><li>网络层协议 <ul><li>IP</li></ul></li><li>传输层协议 <ul><li>TCP</li><li>UDP</li></ul></li><li>应用层协议</li></ul></li></ul></li></ul></li></ul></li></ul><blockquote><p>[!abstract]+ 互联网：从具体组成角度 “网络的网络”</p><ul><li>数以亿计、互联的计算设备 <ul><li>主机 = 端系统 end system host</li><li>运行网络应用程序</li></ul></li><li>通信链路</li><li>光纤、同轴电缆、无线电、卫星</li><li>传输速率=带宽（bps）bit / second</li><li>分组交换设备：转发分组（packets） <ul><li>路由器和交换机</li></ul></li></ul></blockquote><ul><li>Internet 标准 <ul><li>RFC: Request for comments</li><li>IEFC: Internet Engineering Task Force</li></ul></li></ul><blockquote><p>[!abstract]+ 互联网： 从服务角度</p><ol><li>使用通信设施进行通信的分布式应用</li></ol><ul><li>Web、VoIP、email、分布式游戏....</li></ul><ol start="2"><li>通信基础设施为 apps 提供编程接口（通信服务）</li></ol><ul><li>将发送和接收数据的 apps 与互联网连接起来</li><li>为 app 应用提供服务选择类似与邮政服务 <ul><li>无连接 不可靠的服务</li><li>面相连接的可靠服务</li></ul></li></ul></blockquote><h2 id="网络边缘" tabindex="-1"><strong>网络边缘</strong> <a class="header-anchor" href="#网络边缘" aria-label="Permalink to &quot;**网络边缘**&quot;">​</a></h2><h2 id="接入网、物理媒体" tabindex="-1"><strong>接入网、物理媒体</strong> <a class="header-anchor" href="#接入网、物理媒体" aria-label="Permalink to &quot;**接入网、物理媒体**&quot;">​</a></h2><h2 id="网络核心-分组交换、线路交换" tabindex="-1"><strong>网络核心：分组交换、线路交换</strong> <a class="header-anchor" href="#网络核心-分组交换、线路交换" aria-label="Permalink to &quot;**网络核心：分组交换、线路交换**&quot;">​</a></h2>',9)]))}const b=i(o,[["render",u]]);export{h as __pageData,b as default};
